// pages/ProductDetailPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ProductDetailPage = () => {
  const { productId } = useParams(); // Get the productId parameter from the URL
  const [product, setProduct] = useState(null); // State to store the product details

  useEffect(() => {
    // Define a function to fetch product details by productId
    const fetchProductDetails = async () => {
      try {
        const response = await fetch(`/api/products/:${productId}`); // Fetch product details from the backend

        if (response.ok) {
          const productData = await response.json(); // Extract product data from the response
          setProduct(productData); // Update the product state with the fetched product details
        } else {
          // Handle error cases (e.g., product not found)
          console.error('Failed to fetch product details');
        }
      } catch (error) {
        console.error('Error fetching product details:', error);
      }
    };

    // Call the fetchProductDetails function
    fetchProductDetails();
  }, [productId]); // Dependency array with productId to re-run the effect when productId changes

  return (
    <div>
      <h2>Product Detail</h2>
      {product ? (
        <div>
          <p>Product ID: {product.id}</p>
          <p>Product Name: {product.name}</p>
          {/* Display other product details */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProductDetailPage;
