import React, { useState, useEffect } from 'react';
import './FilterComponent.css';
//import Product from './Product';

const FilterComponent = () => {
  const [products, setProducts] = useState([]);
  const [filterCriteria, setFilterCriteria] = useState('');
  const [sortedData, setSortedData] = useState([]);
  const [editingProductId, setEditingProductId] = useState(null);
  const [newProduct, setNewProduct] = useState({
    img: '',
    name: '',
    desc: '',
    price: '',
    year: '',
  });
  const [newProductFormVisible, setNewProductFormVisible] = useState(false);

  useEffect(() => {
    fetch('http://localhost:3000/api/products')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const handleFilterChange = (e) => {
    setFilterCriteria(e.target.value);
  };

  const handleSort = () => {
    const sorted = [...filteredData].sort((a, b) => a.year - b.year);
    setSortedData(sorted);
  };

  const filteredData = products.filter((item) =>
    item.name.toLowerCase().includes(filterCriteria.toLowerCase())
  );

  const handleAddProduct = () => {
    fetch('http://localhost:3000/api/products', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newProduct),
    })
      .then(response => response.json())
      .then(data => {
        setProducts([...products, data]);
        setNewProduct({
          img: '',
          name: '',
          desc: '',
          price: '',
          year: '',
        });
        setNewProductFormVisible(false);
      })
      .catch(error => console.error('Error adding product:', error));
  };

  const handleEdit = (productId) => {
    setEditingProductId(productId);
  };

  const handleSaveEdit = (editedProduct) => {
    fetch(`http://localhost:3000/api/products/${editedProduct.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(editedProduct),
    })
      .then(response => response.json())
      .then(data => {
        const updatedProducts = products.map(product =>
          product.id === data.id ? data : product
        );
        setProducts(updatedProducts);
        setEditingProductId(null);
      })
      .catch(error => console.error('Error saving edited product:', error));
  };

  const handleCancelEdit = () => {
    setEditingProductId(null);
  };

  const handleDelete = (productId) => {
    fetch(`http://localhost:3000/api/products/${productId}`, {
      method: 'DELETE',
    })
      .then(() => {
        const updatedProducts = products.filter(product => product.id !== productId);
        setProducts(updatedProducts);
      })
      .catch(error => console.error('Error deleting product:', error));
  };

  const handleInputChange = (e, productId) => {
    const { name, value } = e.target;
    const updatedProducts = products.map(product =>
      product.id === productId ? { ...product, [name]: value } : product
    );
    setProducts(updatedProducts);
  };

  return (
    <div className="filter-container">
      <input
        type="text"
        placeholder="Search by Product Name"
        value={filterCriteria}
        onChange={handleFilterChange}
      />
      <button onClick={handleSort}>Sort by Year</button>
      <button onClick={() => setNewProductFormVisible(true)}>Add New Product</button>
      {newProductFormVisible && (
        <div>
          <input
            type="text"
            placeholder="Image URL"
            name="img"
            value={newProduct.img}
            onChange={(e) => setNewProduct({ ...newProduct, img: e.target.value })}
          />
          <input
            type="text"
            placeholder="Product Name"
            name="name"
            value={newProduct.name}
            onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Product Description"
            name="desc"
            value={newProduct.desc}
            onChange={(e) => setNewProduct({ ...newProduct, desc: e.target.value })}
          />
          <input
            type="text"
            placeholder="Product Year"
            name="year"
            value={newProduct.year}
            onChange={(e) => setNewProduct({ ...newProduct, year: e.target.value })}
          />
          <input
            type="text"
            placeholder="Price"
            name="price"
            value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
          />
          <button onClick={handleAddProduct}>Save</button>
          <button onClick={() => setNewProductFormVisible(false)}>Cancel</button>
        </div>
      )}

      <ul className="product-list">
        {sortedData.length
          ? sortedData.map((item) => (
              <li className="product" key={item.id}>
                {/* Render product item */}
                {item.img}
                <h1>Name: {item.name}</h1>
              </li>
            ))
          : filteredData.map((item) => (
              <li className="product" key={item.id}>
                {/* Render product item */}
                {item.im}
                <h1>Name: {item.name}</h1>
              </li>
            ))}
      </ul>
    </div>
  );
};

export default FilterComponent;
