import React, { useState, useEffect } from 'react';

const ProductsList = () => {
  const [products, setProducts] = useState(["test"]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:3000/api/products`)

      .then(response => {
        response
        //console.log(response.json());
        setLoading(false);


        return response.json();
      })
    .then(data => {
        console.log(data); // Log parsed data
        setProducts(data); // Update state with parsed data
        //console.log(products)
        setLoading(false); // Update loading state
      })

      .catch(error => {
        console.error('Error fetching products:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Products List</h2>
      <p>products are {products} </p>
      {Array.isArray(products) && products.length > 0 ? ( // Check if products is an array and not empty
        <ul>
          {products.map(product => (
            <li key={product.id}>
              <p>Product ID: {product.id}</p>
              <p>Product Name: {product.name}</p>
              {/* Add other product details */}
            </li>
          ))}
        </ul>
      ) : (
        <p>No products available.</p>
      )}
    </div>
  );
};

export default ProductsList;
